155         bool criaBipartido(ofstream& outFile, int ordem, bool arestaPonderada, bool direcionado, int grau, int componentesConexas, int ponte, int articulacao) {
156             int tamanho[componentesConexas] = {0};
157             int base = ordem / componentesConexas;
158             int sobra = ordem % componentesConexas;
159             int somaTamanhoComponentes = 0;
160 
161             int vet[ordem] = {0};
162             if (!direcionado)
163             {
164                 for(int i = 0; i < ordem; i+=1)
165                 {
166                     vet[i] = rand() % 100 + 1;
167                 }
168             }
169 
170             for (int i = 0; i < componentesConexas; i+=1)
171             {
172                 tamanho[i] = base;
173             }
174             for (int i = 0; i < sobra; i+=1)
175             {
176                 tamanho[i]+=1;
177             }
178             for (int i = 0; i < componentesConexas; i+=1)
179             {
180                 while (tamanho[i] < grau + 1 && tamanho[i] > 1)
181                 {
182                     bool ajustado = false;
183                     for (int j = 0; j < componentesConexas; j+=1)
184                     {
185                         if (i != j && tamanho[j] > 1)
186                         {
187                             tamanho[j]-=1;
188                             tamanho[i]+=1;
189                             ajustado = true;
190                             break;
191                         }
192                     }
193                     if (!ajustado)
194                     {
195                         break;
196                     }
197                 }
198             }
199             int contador=0;
200             for(int i = 0; i<componentesConexas; i+=1)
201             {
202                 if(tamanho[i]>0)
203                 {
204                     contador+=1;
205                 }
206             }
207             
208             if(contador != componentesConexas)
209             {
210                 return false;
211             }
212 
213             bool temPonte= false;
214             bool temArticulacao = false;
215             bool componenteSatisfazGrau = false;
216             for(int i = 0; i<componentesConexas; i+=1)
217             {
218                 int ladoMaior = (tamanho[i]+1)/2;
219                 int ladoMenor = tamanho[i]-ladoMaior;
220                 int calculoGrauUm = ((ladoMenor*grau)-ladoMaior*2);
221                 
222                 while(ladoMaior < grau)
223                 {
224                     if(ladoMenor > 1)
225                     {
226                         ladoMaior += 1;
227                         ladoMenor -= 1;
228                     }
229                     else
230                     {
231                         break;
232                     }
233                 }
234                 if(ladoMenor>1 && grau <= 1)
235                 {
236                     return false;
237                 }
238                 if(ladoMenor >= 1)
239                 {
240                     
241                     if(ladoMenor == 1 && ladoMaior == 1)
242                     {
243                         
244                         temPonte = true;
245                         if(grau == 1)
246                         {
247                             componenteSatisfazGrau = true;
248                         }
249                         else
250                         {
251                             componenteSatisfazGrau = false;
252                         }
253                     }
254                     else if(ladoMenor == 1 && ladoMaior > 1)
255                     {
256                         
257                         temPonte = true;
258                         temArticulacao = true;
259                         if(grau<=ladoMaior)
260                         {
261                             componenteSatisfazGrau = true;
262                         }
263                         break;
264                     }
265                     else if(calculoGrauUm < 0)
266                     {
267                         componenteSatisfazGrau = true;
268                         temPonte = true;
269                         temArticulacao = true;
270                         break;
271                     }
272                     else if(calculoGrauUm >= 0)
273                     {
274                         componenteSatisfazGrau = true;
275                         temPonte = false;
276                         temArticulacao = false;
277                     }
278                 }
279             }
280             if(temArticulacao != articulacao || temPonte != ponte || grau > ordem ||!componenteSatisfazGrau)
281             {
282                 return false;
283             }
284             
285             for(int i = 0; i<componentesConexas; i+=1)
286             {
287                 int ladoMaior = (tamanho[i]+1)/2;
288                 int ladoMenor = tamanho[i]-ladoMaior;
289                 while(ladoMaior < grau)
290                 {
291                     if(ladoMenor > 1)
292                     {
293                         ladoMaior += 1;
294                         ladoMenor -= 1;
295                     }
296                     else
297                     {
298                         break;
299                     }
300                 }
301                 
302                 
303                 int grauComponente;
304                 if(grau >ladoMaior)
305                 {
306                     grauComponente = ladoMaior;
307                 }
308                 else
309                 {
310                     grauComponente = grau;
311                 }
312                 
313                 int contador = 0;
314                 
315                 if(i>0)
316                 {
317                     somaTamanhoComponentes += tamanho[i-1];
318                 }
319                 
320                 for(int j = 0; j<ladoMenor; j+=1)
321                 {
322                     for(int k= 0; k<grauComponente; k+=1)
323                     {
324                         int calculoVertice = j+ladoMenor+k+1 - contador;
325                         int calculoIndice = j+somaTamanhoComponentes+calculoVertice-1-ladoMenor;
326                         
327                        
328                         if(calculoVertice <= tamanho[i])
329                         {
330                             outFile<<j+1+somaTamanhoComponentes<<" "<<calculoVertice+ somaTamanhoComponentes<<" ";
331                             if(arestaPonderada)
332                             {
333                                 if(direcionado)
334                                 {
335                                     outFile<<(rand() % 100 + 1)<<endl;
336                                 }
337                                 else
338                                 {
339                                     outFile<<vet[calculoIndice]<<endl;
340                                     outFile<<calculoVertice+ somaTamanhoComponentes<<" "<<j+1+somaTamanhoComponentes<< " "<<vet[calculoIndice] <<endl;
341                                 }
342                             }
343                             else
344                             {
345                                 if(!direcionado)
346                                 {
347                                     outFile<<endl;
348                                     outFile<<calculoVertice+ somaTamanhoComponentes<<" "<<j+1+somaTamanhoComponentes<<" ";
349                                 }
350                                 outFile<<endl;
351                             }
352                             
353                         }
354                         else
355                         {
356                             outFile<<j+1+somaTamanhoComponentes<<" "<<calculoVertice-ladoMenor+(ladoMenor-ladoMaior)+ somaTamanhoComponentes<<" ";
357                             if(arestaPonderada)
358                             {
359                                 if(direcionado)
360                                 {
361                                     outFile<<(rand() % 100 + 1)<<endl;
362                                 }
363                                 else
364                                 {
365                                     outFile<<vet[calculoIndice]<<endl;
366                                     outFile<<calculoVertice-ladoMenor+(ladoMenor-ladoMaior) 
367                                     +somaTamanhoComponentes<<" "<<j+1+somaTamanhoComponentes<< " "<<vet[calculoIndice] <<endl;
368                                 }
369                             }
370                             else
371                             {
372                                 if(!direcionado)
373                                 {
374                                     outFile<<endl;
375                                     outFile<<calculoVertice-ladoMenor+(ladoMenor-ladoMaior)+somaTamanhoComponentes<<" "<<j+1+somaTamanhoComponentes<< " ";
376                                 }
377                                 outFile<<endl;
378                             }
379                             
380                         }
381                         
382                     }
383                     if(grau>=ladoMaior)
384                     {
385                         contador+=1; 
386                     }
387                     
388                 }
389             }
390             return true;
391         }
393     void carregaGrafo() {
394         ifstream inFile(grafoText);
395         if(arquivoVazio(grafoText))
396         {
397             return;
398         }
399 
400         int numVertices, direcionado, verticePonderado, arestaPonderada;
401         inFile >> numVertices >> direcionado >> verticePonderado >> arestaPonderada;
402 
403         this->setOrdem(numVertices);
404         this->setDirecionado(direcionado);
405         this->setVerticePonderado(verticePonderado);
406         this->setArestaPonderada(arestaPonderada);
407 
408         if (verticePonderado) {
409             for (int i = 0; i < numVertices; i+=1) {
410                 int peso;
411                 inFile >> peso;
412                 insereVertice(peso);
413 
414             }
415         } 
416         else
417         {
418             for (int i = 0; i < numVertices; i+=1) {
419                 insereVertice(0);
420             }
421         }
422 
423         int origem, destino;
424         while (inFile >> origem >> destino) {
425             if (arestaPonderada) {
426                 int peso;
427                 inFile >> peso;
428                 insereAresta(origem, destino, peso);
429             }
430             else {
431                 insereAresta(origem, destino, 0);
432             }
433         }
434         
435         inFile.close();
436         imprimeGrafo();
437     }
