518         cout<<"Vertices ponderados: "<<verticePonderado()<<endl;
519         cout<<"Arestas ponderadas: "<<arestaPonderada()<<endl;
520         cout<<"Completo: "<<eh_completo()<<endl;
521         cout<<"Bipartido: "<<eh_bipartido()<<endl;
