527     void criaGrafo(int ordem, int grau, bool direcionado, int componentesConexas, bool ponte, bool articulacao, bool completo, bool bipartido, bool arvore) {
528         srand(time(0));
529 
530         // Inserir vértices
531         for (int i = 0; i < ordem; i++) {
532             int pesoAleatorio = (rand() % 100 + 1); // Peso aleatório para o vértice
533             insereVertice(pesoAleatorio);
534         }
535 
536         // Tentar inserir arestas de forma aleatória até que o grafo atenda aos requisitos
537         bool grafoValido = false;
538         while (!grafoValido) {
539             int i = rand() % ordem;
540             int j = rand() % ordem;
541             while (i == j) {
542                 j = rand() % ordem;
543             }
544             int pesoAleatorio = (rand() % 100 + 1); // Peso aleatório para a aresta
545 
546             // Tentar inserir a aresta
547             insereAresta(i, j, pesoAleatorio);
548 
549             // Verificar se o grafo atende aos requisitos
550             grafoValido = grafoPronto(ordem, grau, direcionado, componentesConexas, ponte, articulacao, completo, bipartido, arvore);
551 
552             if (!grafoValido) {
553                 // Se a inserção for inválida, remover a aresta
554                 removeAresta(i, j);
555             }
556         }
557 
558         // Imprimir informações do grafo se ele atender aos requisitos
559         imprimeGrafo();
560     }
562     bool grafoPronto(int ordem, int grau, bool direcionado, int cc, bool ponte, bool articulacao, bool completo, bool bipartido, bool arvore)
563     {
564         if(ordem!=getOrdem())
565         {
566             return false;
567         }
568         if(grau != getGrau())
569         {
570             return false;
571         }
572         if(direcionado != eh_direcionado())
573         {
574             return false;
575         }
576         if(getNConexo() != cc)
577         {
578             return false;
579         }
580         if(ponte != possuiPonte())
581         {
582             return false;
583         }
584         if(articulacao != possuiArticulacao())
585         {
586             return false;
587         }
588         if(completo != eh_completo())
589         {
590             return false;
591         }
592         if(bipartido != eh_bipartido())
593         {
594             return false;
595         }   
596         if(arvore != eh_arvore())
597         {
598             return false;
599         }
600         return true;
601     }
